    function wepsim_nodejs_retfill ( ok, msg )
    {
        var msg_txt = msg.replace(/<br>/g,        '\n')
                         .replace(/<EOF>/g,       '[eof]')
                         .replace(/<[^>]*>/g,     '') ;

        var ret = {
                     ok:   ok,
                     html: msg,
                     msg:  treatHTMLSequences(msg_txt)
                  } ;

        return ret ;
    }

